import React, {useState} from 'react'
import Header from './Components/Header'
import Footer from './Components/Footer'
import Button from '@material-ui/core/Button';
import dayjs from 'dayjs'


export default function PageWhenIs(){

const [isDateSet, setIsDate] = useState(false)

const [birthday, setBirthday] = useState({
    month: '',
    day:'',
    year:'',
})

function handleChange(e){
    const fullDate = e.target.value
    const day = fullDate.slice(8,10)
    const month = fullDate.slice(5,7)
    const year = fullDate.slice(0,4)
    setBirthday({
        month: month,
    day: day,
    year: year,
    })

}

function calculateDays(day, month){
   const nowDay = dayjs().format('DD')
   console.log(nowDay)

}

function handleClick(e){
    e.preventDefault()
    setIsDate(true)
    calculateDays(birthday.day, birthday.month)
}

    return (
        <React.Fragment>
            <Header headerName='When is my Birthday?' />
            <div className="container-input">
                <h1>Insert your birthday: </h1>
                <input onChange={handleChange} type="date" placeholder='DD/MM/YYYY'/>
                <Button variant="outlined" color="secondary" onClick={handleClick}>
        Calculate
      </Button>
            </div>
            {isDateSet ? <h1>Days Left to yor Birthday:23</h1> : null }
            <Footer />
        </React.Fragment>
    )
}
