import React, {useState} from 'react'
import Header from './Components/Header'
import Footer from './Components/Footer'
import Button from '@material-ui/core/Button';
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import isLeapYear from 'dayjs/plugin/isLeapYear'
import minMax from 'dayjs/plugin/minMax'


export default function PageWhenIs(){
dayjs.extend(relativeTime)
dayjs.extend(isLeapYear)
dayjs.extend(minMax)


const [isDateSet, setIsDate] = useState(false)

const [birthday, setBirthday] = useState('')

function handleChange(e){
    const fullDate = e.target.value
    setBirthday(fullDate)

}

function calculateDays(birthday){
    // const isLeap =  dayjs(birthday).isLeapYear()
    const year = dayjs().year()
    const day = birthday.slice(8,10)
    const month = birthday.slice(5,7)
    const date1 = dayjs(`${year}-${month}-${day}`)
    const dayOfWeek = dayjs(birthday).day()
    const difference = date1.diff(dayjs(), 'days')
    console.log(difference)
    if (difference <= 0 ){
    return 365 - Math.abs(difference)
} else {
    let newDifference = difference + 1
    return newDifference
}

}

function handleClick(e){
    e.preventDefault()
    setIsDate(true)
    calculateDays(birthday)
}

    return (
        <React.Fragment>
            <Header headerName='When is my Birthday?' />
            <div className="container-input">
                <h1>Insert your birthday:  </h1>
                <input onChange={handleChange} type="date" placeholder='DD/MM/YYYY'/>
                <Button variant="outlined" color="secondary" onClick={handleClick}>
        Calculate
      </Button>
            </div>
            {isDateSet ? <h1>Days Left to your Birthday: {calculateDays(birthday)}</h1> : null }
            <Footer />
        </React.Fragment>
    )
}
