import React, {useState} from 'react'
import Header from './Components/Header'
import Footer from './Components/Footer'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import isLeapYear from 'dayjs/plugin/isLeapYear'


export default function PageWhenIs(props){
dayjs.extend(relativeTime)
dayjs.extend(isLeapYear)

    //HOOKS
    const [isDateSet, setIsDate] = useState(false)
    const [birthday, setBirthday] = useState('')
    const [isCounter, setCounter] = useState(false)

    const year = dayjs().year()
    const day = birthday.slice(8,10)
    const month = birthday.slice(5,7)
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday','Wednesday', 'Thursday', 'Friday', 'Saturday' ]


function handleChange(e){
    const fullDate = e.target.value
    setBirthday(fullDate)
    setCounter(false)
}





function calculateDays(birthday){
    const isLeap =  dayjs(birthday).isLeapYear()

    const date1 = dayjs(`${year}-${month}-${day}`)
    const difference = date1.diff(dayjs(), 'days')
    console.log(difference)
    if (difference <= 0 ){
        if (isLeap === true ){
            return 366 - Math.abs(difference)
        } else {
    return 365 - Math.abs(difference)
}
} else {
    let newDifference = difference + 1
    return newDifference
}
}

function dayOfTheWeek(){
    let dayOfWeek = dayjs(`${year}-${month}-${day}`).day()
    return daysOfWeek[dayOfWeek]
}

function handleClick(e){
    e.preventDefault()
    setIsDate(true)
    calculateDays(birthday)
}

function setCounterOn(){
// Update the count down every 1 second
var x = setInterval(function() {
    var countDownDate = dayjs(`${year}-${month}-${day}`)
  // Get today's date and time
  var now = new Date().getTime();

  // Find the distance between now and the count down date
  var distance = countDownDate - now;

  // Time calculations for days, hours, minutes and seconds
  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  var seconds = Math.floor((distance % (1000 * 60)) / 1000);

  document.getElementById("countDownClock").innerHTML = days + "d " + hours + "h "
  + minutes + "m " + seconds + "s ";
  if (distance < 0) {
    clearInterval(x);
    document.getElementById("countDownClock").innerHTML = "EXPIRED";
}
}, 1000)
} 
function myStopFunction() {
  clearInterval(x);
}


    return (
        <React.Fragment>
            <Header 
            goToKeeper={()=>{
            props.history.push('/keeper')
            }}
            goToWinb={()=>{
            props.history.push('/whenisbirthday')
            }}
            headerName='When is my Birthday?' />
            <div className="container-input">
                <h1>Insert your birthday:  </h1>
                <TextField
        id="date"
        label="Birthday"
        type="date"
        defaultValue="2017-05-24"
        onChange={handleChange}
        InputLabelProps={{
          shrink: true,
        }}
      />
                <Button variant="outlined" color="secondary" onClick={handleClick}>
        Calculate
      </Button>
            </div>
            {isDateSet ? <h1 id='daysLeft'>Days Left to your Birthday: {calculateDays(birthday)}</h1> : null }
            {isDateSet ? <div><h1>Your Birthday is on a : {dayOfTheWeek(birthday)}</h1>
            <Button 
            onClick={(e)=>{
                e.preventDefault()
                setCounter(true)
                setCounterOn()
            }}
            variant="outlined" 
            color="secondary">
            Set Counter
            </Button></div> : null }
            <h1 
            id='countDownClock'> </h1>
            <Footer />
        </React.Fragment>
    )
}
